select.where:
d.del_flag= '0'
and d.device_num like concat('%', #{deviceNum}, '%')
and d.category_id = #{categoryId}
and g.group_id = #{groupId}
and d.device_name like concat('%', #{deviceName}, '%')

and d.firmware_version like concat('%',
    #{firmwareVersion}, '%')

and d.status = #{status}

and d.owner_id like concat('%', #{ownerId}, '%')

    and create_time between #{params.beginCreateTime} and #{params.endCreateTime}

mapper.update:
update iot_device
device_num = #{deviceNum}, category_id = #{categoryId}, device_name = #{deviceName}, firmware_version = #{firmwareVersion}, owner_id = #{ownerId}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where device_id = #{deviceId}

select.where:
t2.del_flag= '0'
and t2.device_num like concat('%', #{deviceNum}, '%')
and t2.category_id = #{categoryId}

and t2.status = #{status}

and t1.user_id = #{ownerId}
